<?phpfunction send_json($data){    header('Content-Type: application/json');    echo json_encode($data);}function error($text){    header('HTTP/1.0 400 Bad Request');    send_json(array('Error'=>array('text'=>$text)));    die();}function server_error($text, $can_retry=false){    header('HTTP/1.0 500 Internal Server Error');    send_json(array('Server_Error'=>array('text'=>$text, 'can_retry'=>$can_retry)));    die();}