<?phpfunction send_json($data){    header('Content-Type: application/json');    echo json_encode($data);}function error($text){    header('HTTP/1.0 400 Bad Request');    log_message('debug',$text);    send_json(array('error'=>array('text'=>$text,'ref'=>'d'.time())));    die();}function server_error($text, $can_retry=false){    header('HTTP/1.0 500 Internal Server Error');    log_message('error',$text);    send_json(array('server_error'=>array('text'=>$text, 'ref'=>'e'.time(),'can_retry'=>$can_retry)));    die();}